import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [despesas, setDespesas] = useState([]);
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [data, setData] = useState('');

  useEffect(() => {
    fetchDespesas();
  }, []);

  const fetchDespesas = async () => {
    const response = await axios.get('http://localhost:8080/api/despesas');
    setDespesas(response.data);
  };

  const adicionarDespesa = async () => {
    const newDespesa = { descricao, valor: parseFloat(valor), data };
    await axios.post('http://localhost:8080/api/despesas', newDespesa);
    fetchDespesas();
    setDescricao('');
    setValor('');
    setData('');
  };

  const deletarDespesa = async (id) => {
    await axios.deletar(`http://localhost:8080/api/despesas/${id}`);
    fetchDespesas();
  };

  return (
    <div className="App">
      <h1>Gestor de Gastos</h1>
      <div className="form">
        <input
          type="text"
          placeholder="Descrição"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
        />
        <input
          type="number"
          placeholder="Valor"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
        />
        <input
          type="date"
          value={data}
          onChange={(e) => setData(e.target.value)}
        />
        <button onClick={adicionarDespesa}>Adicionar Despesa</button>
      </div>
      <div className="Despesas">
        {despesas.map((despesa) => (
          <div key={despesa.id} className="despesa">
            <p>Descrição: {despesa.descricao}</p>
            <p>Valor: ${despesa.valor}</p>
            <p>Data: {despesa.data}</p>
            <button onClick={() => deletarDespesa(despesa.id)}>Deletar</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
